DROP FUNCTION MONEY_TO_CHINESE;
--/
CREATE DEFINER=`tpadmin_core`@`%` FUNCTION MONEY_TO_CHINESE(
money varchar(255)) RETURNS varchar(255) CHARSET utf8
    SQL SECURITY INVOKER
BEGIN
   DECLARE  qinqing  varchar(60);
   DECLARE  c_money   varchar(12);
   DECLARE  m_string varchar(60) ;
   DECLARE  n_string varchar(40) ;
   DECLARE  b_string varchar(80) DEFAULT '';
   DECLARE  b_strings varchar(80);
   DECLARE  n         CHAR;
   DECLARE  len       int(3) DEFAULT 0;
    DECLARE i         int(3) DEFAULT 0;
    DECLARE tmp       BIGINT;
    DECLARE is_zero   BOOLEAN;
    DECLARE z_count   int(3);
    DECLARE l_money   FLOAT;
    DECLARE l_sign    varchar(10);

  
   SET  m_string  = '分角元拾佰仟万拾佰仟亿';
   SET  n_string  = '壹贰叁肆伍陆柒捌玖';
     SET  qinqing=money;
   SET  l_money = abs(money);
     IF money < 0 THEN
      SET l_sign = '负' ;
     ELSE
      SET l_sign = '';
     END IF;
    SET tmp = round(l_money, 2) * 100;##12300
    SET c_money = rtrim(ltrim(to_char(tmp, '999999999999')))+0;
     
    SET len  = length(c_money);##12
        
    SET is_zero = TRUE;
    SET z_count = 0;
    SET i  = 0;
     WHILE i < len do
      SET i = i + 1;
       SET n = substr(c_money, i, 1);##依次取出c_money中的单个字符
       IF n = '0' THEN
         IF len - i = 6 OR len - i = 2 OR len = i THEN
           IF is_zero THEN
           SET  b_string = substr(b_string, 1, length(b_string) - 1);
         
            SET is_zero   = FALSE;
           END IF;
        
           IF len - i = 6 THEN
           SET  b_string = concat(b_string , '万');
            
           END IF;
           IF len - i = 2 THEN
           SET  b_string = concat(b_string , '元');
           END IF;
           IF len = i THEN
          SET   b_string = concat(b_string , '整');
           END IF;
          SET z_count = 1;
         ELSE
           IF z_count = 0 THEN
           SET  b_string = concat(b_string , '零');
           SET  is_zero   = TRUE;
           END IF;
         SET  z_count = z_count + 1;
         
         END IF;
       ELSE
        SET b_string = concat(b_string , substr(n_string, CAST(n AS SIGNED), 1) ,
                     substr(m_string, len - i + 1, 1));
       SET  z_count   = 1 ;
        SET is_zero   = FALSE;
       
       END IF;
     END WHILE ;
     SET b_strings = concat(l_sign ,b_string) ;
     RETURN b_strings;
END
/
DROP FUNCTION add_months;
--/
CREATE DEFINER=`tpadmin_core`@`%` FUNCTION add_months(
date_time datetime,addMonth double) RETURNS datetime
    SQL SECURITY INVOKER
BEGIN
	#Routine body goes here...
  /*oracle中的ADD_MONTHS(DATE,NUMBER)中的NUMBER应当是整数，给出小数时，正数被截为小于该数的最大整数，负数被截为大于该数的最小整数。
  而mysql中的DATE_ADD中的number假如是小数时，则取法是四舍五入的，故在此需要做转换*/
   DECLARE a INT;
   IF(addMonth>0) THEN
	 SET a=FLOOR(addMonth);
   ELSE SET a=CEIL(addMonth);
   END IF;
   RETURN DATE_ADD(date_time,INTERVAL a MONTH);
END
/
